import { useState } from "react"
import { InputCheckbox } from "../InputCheckbox"
import { TransactionPaneComponent } from "./types"
import React from "react"

export const TransactionPane = ({ transaction, loading, setTransactionApproval }) => {
  const handleApprovalChange = async (newValue: boolean) => {
    // Specify type for newValue
    try {
      // Call setTransactionApproval to update approval status on server
      await setTransactionApproval({ transactionId: transaction.id, newValue })
    } catch (error) {
      console.error("Error while updating approval status:", error)
      // Handle error if needed
    }
  }

  return (
    <div className="RampPane">
      <div className="RampPane--content">
        <p className="RampText">{transaction.merchant}</p>
        <b>{moneyFormatter.format(transaction.amount)}</b>
        <p className="RampText--hushed RampText--s">
          {transaction.employee.firstName} {transaction.employee.lastName} - {transaction.date}
        </p>
      </div>
      <InputCheckbox
        id={transaction.id}
        checked={transaction.approved}
        disabled={loading}
        onChange={handleApprovalChange}
      />
    </div>
  )
}

const moneyFormatter = new Intl.NumberFormat("en-US", {
  style: "currency",
  currency: "USD",
})
