import React from "react"

interface InputCheckboxProps {
  id: string
  checked?: boolean
  disabled?: boolean
  onChange: (newValue: boolean) => void
}

export const InputCheckbox: React.FC<InputCheckboxProps> = ({
  id,
  checked = false,
  disabled = false,
  onChange,
}) => {
  return (
    <div className="RampInputCheckbox--container">
      <label
        className={`RampInputCheckbox--label ${checked ? "RampInputCheckbox--label-checked" : ""} ${
          disabled ? "RampInputCheckbox--label-disabled" : ""
        }`}
      >
        <input
          id={id}
          type="checkbox"
          className="RampInputCheckbox--input"
          checked={checked}
          disabled={disabled}
          onChange={() => onChange(!checked)} // Toggle the value on change
        />
      </label>
    </div>
  )
}
